/**
 * @component StatCard
 * @description Reusable statistics card component with icon and metrics.
 * Used across admin, principal, management, and other role dashboards
 * to display key performance indicators and stats.
 * 
 * @param {Object} props
 * @param {string} props.title - Card title (e.g., "Total Users", "Attendance Today")
 * @param {string|number} props.value - Main metric value to display
 * @param {string} [props.subtitle] - Optional subtitle/secondary information
 * @param {React.Component} props.icon - Lucide React icon component
 * @param {string} [props.color='blue'] - Color theme: 'blue', 'green', 'purple', 'orange', 'red'
 * @param {Function} [props.onClick] - Optional click handler for interactive cards
 * @param {string} [props.className] - Additional CSS classes
 * 
 * @example
 * <StatCard 
 *   title="Total Users"
 *   value={2450}
 *   subtitle="150 staff, 2300 students"
 *   icon={Users}
 *   color="blue"
 *   onClick={() => navigate('/users')}
 * />
 */
import React from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';

const StatCard = ({ 
  title, 
  value, 
  subtitle, 
  icon: Icon, 
  color = 'blue', 
  onClick, 
  className 
}) => {
  // Map color prop to Tailwind utility classes
  const colorClasses = {
    blue: 'bg-blue-50 text-blue-600',
    green: 'bg-green-50 text-green-600',
    purple: 'bg-purple-50 text-purple-600',
    orange: 'bg-orange-50 text-orange-600',
    red: 'bg-red-50 text-red-600',
    cyan: 'bg-cyan-50 text-cyan-600'
  };

  return (
    <div 
      className={clsx(
        // Base styles
        "bg-white p-6 rounded-2xl shadow-sm border border-slate-100",
        "flex items-center gap-4 transition-all duration-200",
        // Interactive styles if onClick provided
        onClick && "cursor-pointer hover:shadow-md hover:scale-[1.02]",
        // Custom classes
        className
      )}
      onClick={onClick}
      role={onClick ? "button" : undefined}
      tabIndex={onClick ? 0 : undefined}
      onKeyPress={onClick ? (e) => e.key === 'Enter' && onClick(e) : undefined}
    >
      {/* Icon container with dynamic color theme */}
      <div className={clsx(
        "p-4 rounded-xl shrink-0",
        colorClasses[color] || colorClasses.blue
      )}>
        <Icon size={24} aria-hidden="true" />
      </div>

      {/* Metrics content */}
      <div className="flex-1 min-w-0">
        <h3 className="text-2xl font-bold text-slate-800 truncate">
          {value}
        </h3>
        <p className="text-xs text-slate-500 font-medium uppercase tracking-wide truncate">
          {title}
        </p>
        {subtitle && (
          <p className="text-xs text-slate-400 mt-1 truncate">
            {subtitle}
          </p>
        )}
      </div>
    </div>
  );
};

StatCard.propTypes = {
  title: PropTypes.string.isRequired,
  value: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.number
  ]).isRequired,
  subtitle: PropTypes.string,
  icon: PropTypes.elementType.isRequired,
  color: PropTypes.oneOf(['blue', 'green', 'purple', 'orange', 'red', 'cyan']),
  onClick: PropTypes.func,
  className: PropTypes.string
};

export default StatCard;

// src/components/navigation/admin/Sidebar.jsx
import React from 'react';
import { NavLink } from 'react-router-dom';
import { 
  LayoutDashboard, Users, UserPlus, Settings, Briefcase, 
  DollarSign, CheckSquare, Bell, FileText, Activity, 
  Server, BarChart, LifeBuoy, Shield, Database, LogOut,
  Building, X 
} from 'lucide-react';

export const ADMIN_MENU_ITEMS = [
  { id: 'ad1', name: "Control Dashboard", path: "/dashboard/admin", icon: <LayoutDashboard size={20} /> },
  { id: 'ad2', name: "Admissions Console", path: "/dashboard/admin/admissions", icon: <UserPlus size={20} /> },
  { id: 'ad3', name: "User Management", path: "/dashboard/admin/users", icon: <Users size={20} /> },
  { id: 'ad4', name: "Class Config", path: "/dashboard/admin/classes", icon: <Building size={20} /> },
  { id: 'ad5', name: "HR Admin", path: "/dashboard/admin/hr", icon: <Briefcase size={20} /> },
  { id: 'ad6', name: "Finance Control", path: "/dashboard/admin/finance", icon: <DollarSign size={20} /> },
  { id: 'ad7', name: "Attendance Oversight", path: "/dashboard/admin/attendance", icon: <CheckSquare size={20} /> },
  { id: 'ad8', name: "Notification Center", path: "/dashboard/admin/notifications", icon: <Bell size={20} /> },
  { id: 'ad9', name: "Compliance Vault", path: "/dashboard/admin/compliance", icon: <FileText size={20} /> },
  { id: 'ad10', name: "Audit Trail", path: "/dashboard/admin/audit", icon: <Activity size={20} /> },
  { id: 'ad11', name: "Infrastructure", path: "/dashboard/admin/infrastructure", icon: <Server size={20} /> },
  { id: 'ad12', name: "Data Recovery", path: "/dashboard/admin/backup", icon: <Database size={20} /> },
  { id: 'ad13', name: "Analytics Engine", path: "/dashboard/admin/analytics", icon: <BarChart size={20} /> },
  { id: 'ad14', name: "System Config", path: "/dashboard/admin/settings", icon: <Settings size={20} /> },
  { id: 'ad15', name: "Support Tickets", path: "/dashboard/admin/helpdesk", icon: <LifeBuoy size={20} /> },
];

const Sidebar = ({ isMobileMenuOpen, setIsMobileMenuOpen, onLogout }) => {

  return (
    <>
      {/* Mobile Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="fixed inset-0 bg-black/50 z-40 md:hidden"
          onClick={() => setIsMobileMenuOpen && setIsMobileMenuOpen(false)}
        />
      )}

      {/* Sidebar Container */}
      <div className={`
        fixed inset-y-0 left-0 z-50 w-72 p-4 flex flex-col transition-transform duration-300 ease-in-out
        ${isMobileMenuOpen ? 'translate-x-0' : '-translate-x-full md:translate-x-0'}
      `}>
         {/* Gradient Box - Updated to match Screenshot (Cyan -> Blue -> Pink) */}
         <div className="h-full w-full rounded-3xl bg-gradient-to-b from-cyan-400 via-blue-400 to-pink-400 p-4 flex flex-col text-white shadow-2xl relative overflow-hidden">
            
            {/* Glass Effects */}
            <div className="absolute -top-10 -right-10 w-40 h-40 bg-white opacity-10 rounded-full blur-2xl"></div>
            <div className="absolute bottom-10 -left-10 w-40 h-40 bg-pink-500 opacity-20 rounded-full blur-2xl"></div>

            {/* Close Button for Mobile */}
            <button 
               onClick={() => setIsMobileMenuOpen && setIsMobileMenuOpen(false)}
               className="absolute top-4 right-4 p-2 text-white/80 hover:text-white hover:bg-white/10 rounded-full transition-colors z-50 md:hidden"
            >
               <X size={20} />
            </button>

            {/* Header */}
            <div className="flex items-center gap-3 mb-8 pl-2 relative z-10">
               <div className="p-2 bg-white/20 rounded-lg backdrop-blur-sm shadow-sm">
                  <Shield size={24} className="text-white" />
               </div>
               <div>
                  <h1 className="font-bold text-lg tracking-wide">Krimson OS</h1>
                  <p className="text-xs text-white/80">Singapore</p>
               </div>
            </div>

            {/* Navigation List */}
            <div className="flex-1 overflow-y-auto space-y-1 relative z-10 custom-scrollbar pr-1">
               {ADMIN_MENU_ITEMS.map((item) => (
                  <NavLink
                     key={item.id}
                     to={item.path}
                     end={item.path === '/dashboard/admin'} 
                     onClick={() => setIsMobileMenuOpen && setIsMobileMenuOpen(false)}
                     className={({ isActive }) => `
                        flex items-center gap-3 px-4 py-3 rounded-xl transition-all duration-200 group
                        ${isActive 
                           ? "bg-white text-blue-600 shadow-md font-bold" 
                           : "text-white/90 hover:bg-white/10 hover:translate-x-1"
                        }
                     `}
                  >
                     <span className={({ isActive }) => isActive ? "text-blue-500" : "text-white"}>{item.icon}</span>
                     <span className="text-sm">{item.name}</span>
                  </NavLink>
               ))}
            </div>

            {/* Footer Profile */}
            <div className="mt-4 pt-4 border-t border-white/20 relative z-10">
               <div className="flex items-center gap-3 p-3 rounded-xl bg-white/10 backdrop-blur-md hover:bg-white/20 transition-colors">
                  <NavLink to="/dashboard/admin/profile" className="flex items-center gap-3 flex-1 group cursor-pointer">
                     <div className="w-8 h-8 rounded-full bg-white text-blue-600 flex items-center justify-center font-bold text-xs shadow-sm group-hover:scale-110 transition-transform">A</div>
                     <div className="flex-1">
                        <p className="text-sm font-semibold text-white group-hover:text-blue-100 transition-colors">Administrator</p>
                        <p className="text-[10px] text-white/80">:: Verified</p>
                     </div>
                  </NavLink>
                  <button 
                     onClick={onLogout}
                     className="p-2 text-white/70 hover:text-white hover:bg-white/10 rounded-lg transition-colors"
                     title="Logout"
                  >
                     <LogOut size={18} />
                  </button>
               </div>
            </div>
         </div>
      </div>
    </>
  );
};

export default Sidebar;

/**
 * @file examples/ReduxExample.jsx
 * @description Example component demonstrating Redux Toolkit usage.
 * Shows how to use Redux state and dispatch actions.
 * 
 * THIS IS AN EXAMPLE FILE - Use as reference for implementing Redux in your components
 */

import React, { useEffect, useCallback } from 'react';
import PropTypes from 'prop-types';
import { useAppDispatch, useAppSelector } from '../store/hooks';
import { loginUser, logoutUser, setCurrentRole } from '../store/slices/authSlice';
import { toggleSidebar, addNotification, setLoading } from '../store/slices/uiSlice';
import { fetchAdminDashboard } from '../store/slices/dashboardSlice';

/**
 * @component ReduxExample
 * @description Example showing Redux integration patterns
 */
const ReduxExample = () => {
  const dispatch = useAppDispatch();
  
  // ============================================
  // READING STATE FROM REDUX STORE
  // ============================================
  
  // Auth state
  const { user, isAuthenticated, currentRole, loading: authLoading } = useAppSelector(
    state => state.auth
  );
  
  // UI state
  const { sidebarOpen, theme, notifications } = useAppSelector(
    state => state.ui
  );
  
  // Dashboard state
  // Dashboard state
  const { loading: dashboardLoading } = useAppSelector(
    state => state.dashboard
  );
  
  // ============================================
  // DISPATCHING ACTIONS
  // ============================================
  
  /**
   * Example: Login user
   */
  const handleLogin = async () => {
    try {
      // Dispatch async thunk
      await dispatch(loginUser({
        email: 'admin@school.com',
        password: 'password123'
      })).unwrap(); // .unwrap() throws on rejection
      
      // Success - show notification
      dispatch(addNotification({
        type: 'success',
        message: 'Login successful!'
      }));
    } catch (error) {
      // Error handling
      dispatch(addNotification({
        type: 'error',
        message: error.message || 'Login failed'
      }));
    }
  };
  
  /**
   * Example: Logout user
   */
  const handleLogout = () => {
    dispatch(logoutUser());
    dispatch(addNotification({
      type: 'info',
      message: 'You have been logged out'
    }));
  };
  
  /**
   * Example: Switch role
   */
  const handleRoleSwitch = (role) => {
    dispatch(setCurrentRole(role));
    dispatch(addNotification({
      type: 'info',
      message: `Switched to ${role} dashboard`
    }));
  };
  
  /**
   * Example: Toggle sidebar
   */
  const handleToggleSidebar = () => {
    dispatch(toggleSidebar());
  };
  
  /**
   * Example: Fetch dashboard data
   */
  /**
   * Example: Fetch dashboard data
   */
  const handleFetchDashboard = useCallback(async () => {
    try {
      dispatch(setLoading({ feature: 'dashboard', isLoading: true }));
      
      await dispatch(fetchAdminDashboard()).unwrap();
      
      dispatch(addNotification({
        type: 'success',
        message: 'Dashboard data loaded'
      }));
    } catch (error) {
      dispatch(addNotification({
        type: 'error',
        message: 'Failed to load dashboard'
      }));
    } finally {
      dispatch(setLoading({ feature: 'dashboard', isLoading: false }));
    }
  }, [dispatch]);
  
  /**
   * Example: Fetch dashboard on component mount
   */
  useEffect(() => {
    if (isAuthenticated && currentRole === 'admin') {
      handleFetchDashboard();
    }
  }, [isAuthenticated, currentRole, handleFetchDashboard]);
  
  // ============================================
  // RENDER EXAMPLE UI
  // ============================================
  
  return (
    <div className="p-8 max-w-4xl mx-auto">
      <div className="card-base mb-6">
        <h1 className="text-2xl font-bold mb-4">Redux State Management Example</h1>
        <p className="text-slate-600 mb-4">
          This component demonstrates how to use Redux Toolkit in your app.
        </p>
        
        {/* Auth State Display */}
        <div className="bg-slate-50 p-4 rounded-lg mb-4">
          <h3 className="font-bold mb-2">Auth State:</h3>
          <pre className="text-xs overflow-auto">
            {JSON.stringify({
              isAuthenticated,
              currentRole,
              user: user?.name || 'Not logged in'
            }, null, 2)}
          </pre>
        </div>
        
        {/* UI State Display */}
        <div className="bg-slate-50 p-4 rounded-lg mb-4">
          <h3 className="font-bold mb-2">UI State:</h3>
          <pre className="text-xs overflow-auto">
            {JSON.stringify({
              sidebarOpen,
              theme,
              notificationsCount: notifications.length
            }, null, 2)}
          </pre>
        </div>
        
        {/* Action Buttons */}
        <div className="flex flex-wrap gap-3">
          <button
            onClick={handleLogin}
            className="btn-primary px-4 py-2 text-sm"
            disabled={authLoading}
          >
            {authLoading ? 'Logging in...' : 'Login Example'}
          </button>
          
          <button
            onClick={handleLogout}
            className="btn-secondary px-4 py-2 text-sm"
          >
            Logout Example
          </button>
          
          <button
            onClick={handleToggleSidebar}
            className="btn-secondary px-4 py-2 text-sm"
          >
            Toggle Sidebar: {sidebarOpen ? 'Open' : 'Closed'}
          </button>
          
          <button
            onClick={handleFetchDashboard}
            className="btn-primary px-4 py-2 text-sm"
            disabled={dashboardLoading}
          >
            {dashboardLoading ? 'Loading...' : 'Fetch Dashboard'}
          </button>
          
          <button
            onClick={() => handleRoleSwitch('teacher')}
            className="btn-secondary px-4 py-2 text-sm"
          >
            Switch to Teacher
          </button>
        </div>
      </div>
      
      {/* Notifications Display */}
      {notifications.length > 0 && (
        <div className="space-y-2">
          <h3 className="font-bold">Notifications:</h3>
          {notifications.map(notif => (
            <div
              key={notif.id}
              className={`p-3 rounded-lg ${
                notif.type === 'success' ? 'bg-green-50 text-green-700' :
                notif.type === 'error' ? 'bg-red-50 text-red-700' :
                'bg-blue-50 text-blue-700'
              }`}
            >
              {notif.message}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

ReduxExample.propTypes = {};

export default ReduxExample;

import React, { useState } from "react";
import { Shield, Lock, Globe, AlertCircle, ArrowRight, Key } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { authService } from "../../services/authService";
import { useAppDispatch } from "../../store/hooks";
import { addNotification } from "../../store/slices/uiSlice";

/**
 * UNIFIED LOGIN PORTAL
 * Centralizes access for all roles with SSO-style authentication and 2FA simulation.
 */
const LoginPage = () => {
  const [step, setStep] = useState(1); // 1 = Credentials, 2 = OTP/2FA
  const [authEmail, setAuthEmail] = useState("admin@krimson.com");
  const [authPassword, setAuthPassword] = useState("admin");
  const [otp, setOtp] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const navigate = useNavigate();
  const dispatch = useAppDispatch();

  // STEP 1: VALIDATE CREDENTIALS
  const handleCredentialSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      if (!authEmail || !authPassword) {
        throw new Error("Please enter both email and password");
      }

      // Call API to verify credentials
      const response = await authService.login(authEmail, authPassword);
      
      // On success, prepare for Step 2
      setStep(2); // Move to OTP
      dispatch(addNotification({ type: 'info', message: 'Credentials verified. Please enter OTP.' }));
      
    } catch (err) {
      console.error(err);
      const msg = err.message || "Login failed. Invalid credentials.";
      setError(msg);
      dispatch(addNotification({ type: 'error', message: msg }));
    } finally {
      setLoading(false);
    }
  };

  // STEP 2: VERIFY OTP (SIMULATION)
  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    // Simulate OTP verification delay for "PDPA Compliance" feel
    setTimeout(() => {
      // In a real app, we would verify OTP with backend here
      if (otp !== "1234") {
        setError("Invalid OTP. Please enter '1234'");
        setLoading(false);
        return;
      }

      setLoading(false);
      dispatch(addNotification({ type: 'success', message: 'Identity Verified. Redirecting...' }));
      
      // REDIRECT TO WELCOME LANDING
      navigate("/welcome");

    }, 1500);
  };

  const handleForgotPassword = () => {
    navigate("/forgot-password");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-cyan-300 via-blue-300 to-pink-300 flex items-center justify-center p-2 sm:p-4 font-sans selection:bg-blue-100">
      {/* Main Glass Card Container */}
      <div className="max-w-6xl w-full bg-white/80 backdrop-blur-xl rounded-2xl sm:rounded-[3rem] shadow-2xl shadow-purple-900/10 overflow-hidden flex flex-col lg:flex-row min-h-[500px] sm:min-h-[600px] border border-white/50">
        
        {/* LEFT SIDEBAR: Branding - Unchanged */}
        <div className="lg:w-5/12 flex flex-col justify-between relative overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-b from-cyan-300 via-blue-300 to-pink-300"></div>
          <div className="relative z-10 p-6 sm:p-8 lg:p-12 text-slate-800">
            <div className="w-10 h-10 sm:w-12 sm:h-12 bg-white/40 backdrop-blur rounded-xl sm:rounded-2xl flex items-center justify-center mb-4 sm:mb-6 shadow-sm">
              <Shield className="h-6 w-6 sm:h-7 sm:w-7 text-slate-800" />
            </div>
            <h1 className="text-2xl sm:text-3xl lg:text-4xl font-bold mb-2 tracking-tight">
              Krimson OS
            </h1>
            <p className="text-slate-700 text-sm sm:text-base lg:text-lg">
              Centralized School Ecosystem
            </p>
          </div>
          <div className="relative z-10 p-6 sm:p-8 lg:p-12 space-y-3 sm:space-y-4">
            <div className="flex items-center gap-2 sm:gap-3 text-slate-700">
              <div className="p-1.5 sm:p-2 bg-white/40 rounded-lg shadow-sm">
                <Lock className="h-3 w-3 sm:h-4 sm:w-4 text-slate-800" />
              </div>
              <span className="font-medium text-sm sm:text-base">
                Secure SSO Gateway
              </span>
            </div>
            <div className="flex items-center gap-2 sm:gap-3 text-slate-700">
              <div className="p-1.5 sm:p-2 bg-white/40 rounded-lg shadow-sm">
                <Globe className="h-3 w-3 sm:h-4 sm:w-4 text-slate-800" />
              </div>
              <span className="font-medium text-sm sm:text-base">
                7 Apps Connected
              </span>
            </div>
            <p className="pt-2 text-left text-xs text-slate-600">
              Restricted Access ΓÇó Krimson OS v2.4 ΓÇó Singapore
            </p>
          </div>
        </div>

        {/* RIGHT SIDE: UNIFIED LOGIN FORM */}
        <div className="lg:w-7/12 p-6 sm:p-8 lg:p-12 xl:p-16 flex flex-col justify-center relative">
          
          <div className="max-w-md w-full mx-auto">
            <h2 className="text-2xl sm:text-3xl font-bold text-slate-900 mb-2">
              {step === 1 ? "Authorized Login" : "Two-Factor Auth"}
            </h2>
            <p className="text-slate-500 mb-6 sm:mb-8 text-sm sm:text-base">
              {step === 1 
                ? "Access the Singapore Campus Portal via SSO."
                : "Please enter the OTP sent to your device."
              }
            </p>

            {error && (
              <div className="mb-6 p-4 bg-rose-50 border border-rose-100 text-rose-600 rounded-xl text-sm flex items-center gap-2 animate-in fade-in slide-in-from-top-2">
                <AlertCircle className="h-4 w-4 shrink-0" />
                {error}
              </div>
            )}

            {/* STEP 1: EMAIL & PASSWORD */}
            {step === 1 && (
              <form onSubmit={handleCredentialSubmit} className="space-y-5 animate-in fade-in slide-in-from-right-4 duration-300">
                <div>
                  <label className="block text-sm font-bold text-slate-700 mb-1.5">
                    Email / User ID
                  </label>
                  <input
                    type="text"
                    value={authEmail}
                    onChange={(e) => setAuthEmail(e.target.value)}
                    className="w-full p-3.5 border border-slate-200 bg-slate-50 rounded-xl focus:ring-2 focus:ring-blue-500 focus:bg-white outline-none transition-all"
                    placeholder="e.g. admin@krimson.edu"
                    autoFocus
                  />
                </div>
                <div>
                  <div className="flex justify-between items-center mb-1.5">
                    <label className="block text-sm font-bold text-slate-700">
                      Password
                    </label>
                  </div>
                  <input
                    type="password"
                    value={authPassword}
                    onChange={(e) => setAuthPassword(e.target.value)}
                    className="w-full p-3.5 border border-slate-200 bg-slate-50 rounded-xl focus:ring-2 focus:ring-blue-500 focus:bg-white outline-none transition-all font-sans"
                    placeholder="ΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇó"
                  />
                </div>
                
                <button
                  type="submit"
                  disabled={loading}
                  className="w-full py-3.5 bg-blue-600 hover:bg-blue-700 text-white rounded-xl font-bold shadow-lg shadow-blue-600/20 transition-all disabled:opacity-70 disabled:cursor-not-allowed flex items-center justify-center gap-2 mt-4"
                >
                  {loading ? (
                    "Authenticating..."
                  ) : (
                    <>
                      Continue <ArrowRight className="h-4 w-4" />
                    </>
                  )}
                </button>
                
                <button 
                  type="button" 
                  onClick={handleForgotPassword}
                  className="w-full text-center text-sm font-medium text-slate-500 hover:text-blue-600 transition-colors mt-2"
                >
                  Forgot Password?
                </button>
              </form>
            )}

            {/* STEP 2: OTP / 2FA */}
            {step === 2 && (
              <form onSubmit={handleOtpSubmit} className="space-y-5 animate-in fade-in slide-in-from-right-4 duration-300">
                <div className="p-4 bg-blue-50 border border-blue-100 rounded-xl flex gap-3 items-start">
                   <div className="p-2 bg-white rounded-lg shadow-sm text-blue-600">
                     <Shield className="h-5 w-5" />
                   </div>
                   <div>
                     <p className="text-sm font-bold text-slate-800">PDPA Compliance Verify</p>
                     <p className="text-xs text-slate-500">
                       We've sent a 6-digit code to your registered device.
                     </p>
                   </div>
                </div>

                <div>
                  <label className="block text-sm font-bold text-slate-700 mb-1.5">
                    One-Time Password
                  </label>
                  <div className="relative">
                    <Key className="absolute left-3.5 top-3.5 h-5 w-5 text-slate-400" />
                    <input
                      type="text"
                      value={otp}
                      onChange={(e) => setOtp(e.target.value)}
                      className="w-full p-3.5 pl-11 border border-slate-200 bg-slate-50 rounded-xl focus:ring-2 focus:ring-blue-500 focus:bg-white outline-none transition-all tracking-widest font-mono text-lg"
                      placeholder="XXXXXX"
                      autoFocus
                    />
                  </div>
                </div>

                <button
                  type="submit"
                  disabled={loading}
                  className="w-full py-3.5 bg-gradient-to-r from-emerald-500 to-teal-500 hover:from-emerald-600 hover:to-teal-600 text-white rounded-xl font-bold shadow-lg shadow-emerald-500/20 transition-all disabled:opacity-70 disabled:cursor-not-allowed flex items-center justify-center gap-2 mt-4"
                >
                  {loading ? (
                    "Verifying..."
                  ) : (
                    <>
                      Verify Identity <Shield className="h-4 w-4" />
                    </>
                  )}
                </button>
                
                <button 
                  type="button"
                  onClick={() => { setStep(1); setError(""); }}
                  className="w-full text-center text-sm text-slate-500 hover:text-slate-800 font-medium py-2"
                >
                  Back to Login
                </button>
              </form>
            )}
            
            {/* Version / Info Footer */}
            <div className="mt-8 pt-6 border-t border-slate-100 text-center">
              <p className="text-xs text-slate-400">
                Authorized Use Only. System Activity Monitored.
              </p>
            </div>

          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;

import React, { useState, useEffect, useCallback } from "react";
import {
  UserPlus,
  RefreshCw,
  CheckSquare,
  Edit3,
  UserX,
  AlertCircle
} from "lucide-react";
import { userService } from "../../../services/userService";
import { useAppDispatch } from "../../../store/hooks";
import { addNotification } from "../../../store/slices/uiSlice";

// Components
import ActionDropdown from "./components/ActionDropdown";
import RoleMultiSelector from "./components/RoleMultiSelector";
import UserSearchPanel from "./components/UserSearchPanel";
import UserListTable from "./components/UserListTable";

const Card = ({ children, className = "" }) => (
  <div
    className={`bg-white rounded-[2rem] p-6 shadow-sm border border-slate-100 ${className}`}
  >
    {children}
  </div>
);

const UserManagement = () => {
  const dispatch = useAppDispatch();

  // State for Database Data
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [roles, setRoles] = useState([]); // State for roles

  // Pagination State
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    totalUsers: 0,
  });

  // Constants
  const VALID_ROLES = [
    { label: "Student", value: "STUDENT" },
    { label: "Teacher", value: "TEACHER" },
    { label: "Parent", value: "PARENT" },
    { label: "Administrator", value: "ADMINISTRATOR" },
    { label: "Principal", value: "PRINCIPAL" },
    { label: "Finance Officer", value: "FINANCE_OFFICER" },
    { label: "Registrar", value: "REGISTRAR" },
    { label: "Academic Coordinator", value: "ACADEMIC_COORDINATOR" },
    { label: "Counselor", value: "COUNSELOR" },
    { label: "Librarian", value: "LIBRARIAN" },
    { label: "Management", value: "MANAGEMENT" },
    { label: "IT/System Admin", value: "IT_ADMIN" },
  ];

  const LEGACY_ROLE_MAP = {
    "FINANCE": "FINANCE_OFFICER",
    "FINANCE OFFICER": "FINANCE_OFFICER",
    "ACADEMIC COORDINATOR": "ACADEMIC_COORDINATOR",
    "IT": "IT_ADMIN",
    "IT/SYSTEM ADMIN": "IT_ADMIN",
    "IT ADMIN": "IT_ADMIN",
    // Code mappings for robustness
    "R01": "STUDENT",
    "R02": "TEACHER",
    "R03": "PARENT",
    "R04": "PRINCIPAL",
    "R05": "ADMINISTRATOR",
    "R06": "FINANCE_OFFICER",
    "R07": "REGISTRAR",
    "R08": "MANAGEMENT",
    "R09": "ACADEMIC_COORDINATOR",
    "R10": "COUNSELOR",
    "R11": "LIBRARIAN",
    "R12": "IT_ADMIN",
  };

  const FETCH_LIMIT = 100; // Fetch more users to have enough data for local pagination
  const ROWS_PER_PAGE = 10; // Show exactly 10 rows per page

  // Local Pagination State
  const [localPage, setLocalPage] = useState(1);

  // Action Mode State - Controls which form to display
  const [actionMode, setActionMode] = useState("add"); // "add" | "edit" | "suspend"
  // Note: isDropdownOpen logic moved to ActionDropdown component

  // Form State for Add User
  const [newUser, setNewUser] = useState({
    full_name: "",
    role: "Student",
    email: "",
  });

  // Search State for Edit/Suspend
  const [searchEmail, setSearchEmail] = useState("");
  const [searchedUser, setSearchedUser] = useState(null);
  const [searching, setSearching] = useState(false);
  const [searchError, setSearchError] = useState("");

  // Edit Form State
  const [editForm, setEditForm] = useState({
    full_name: "",
    roles: [], // Array of selected roles
    email: "",
  });

  // Switch action mode
  const switchActionMode = (mode) => {
    setActionMode(mode);
    // Reset all forms when switching
    setSearchEmail("");
    setSearchedUser(null);
    setSearchError("");
    setNewUser({ full_name: "", role: "Student", email: "" });
    setEditForm({ full_name: "", roles: [], email: "" });
  };

  // Search user by email (for Edit/Suspend)
  const handleSearchUser = async () => {
    if (!searchEmail.trim()) {
      setSearchError("Please enter an email address");
      return;
    }

    setSearching(true);
    setSearchError("");
    setSearchedUser(null);

    try {
      const result = await userService.getUserByIdentifier(searchEmail);

      if (result.success && result.data) {
        setSearchedUser(result.data);
        // Only populate edit form if in edit mode (or just do it anyway, harmless)
        // Aggressive Role Cleaning: Deduplicate & Filter only valid roles
        const rawRoles = result.data.roles || [result.data.active_role || result.data.role];
        const validRoleValues = VALID_ROLES.map(r => r.value);
        
        const cleanedRoles = [...new Set(
          rawRoles
            .filter(Boolean)
            .map(r => {
                if (!r) return r;
                const upper = r.toUpperCase();
                return LEGACY_ROLE_MAP[upper] || upper;
            })
            .filter(r => validRoleValues.includes(r))
        )];

        setEditForm({
          full_name: result.data.full_name,
          roles: cleanedRoles,
          email: result.data.email,
        });
      } else {
        setSearchError("User not found with this email address");
      }
    } catch (err) {
      console.error(err);
      if (err.response?.status === 404) {
        setSearchError("User not found with this email address");
      } else {
        setSearchError(err.message || "Error searching user");
      }
    } finally {
      setSearching(false);
    }
  };

  // Handle Edit User
  const handleEditUser = async () => {
    if (!searchedUser) {
      dispatch(
        addNotification({
          type: "warning",
          message: "Please search for a user first",
        })
      );
      return;
    }

    try {
      const result = await userService.updateUser(searchedUser.email, {
        full_name: editForm.full_name,
        roles: editForm.roles,
      });

      if (result.success) {
        dispatch(
          addNotification({
            type: "success",
            message: "User roles updated successfully!",
          })
        );

        // Reset form and switch back to add mode
        switchActionMode("add");

        // Refresh users list
        fetchUsers(pagination.currentPage);
      } else {
        dispatch(
          addNotification({
            type: "error",
            message: result.message || "Error updating user",
          })
        );
      }
    } catch (err) {
      console.error(err);
      dispatch(
        addNotification({
          type: "error",
          message: err.message || "Error updating user",
        })
      );
    }
  };

  // Handle Suspend User
  const handleSuspendUser = async () => {
    if (!searchedUser) {
      dispatch(
        addNotification({
          type: "warning",
          message: "Please search for a user first",
        })
      );
      return;
    }

    try {
      const result = await userService.suspendUser(searchedUser.email);

      if (result.success) {
        dispatch(
          addNotification({
            type: "success",
            message: `User ${searchedUser.email} suspended successfully!`,
          })
        );

        // Reset form and switch back to add mode
        switchActionMode("add");

        // Refresh users list
        fetchUsers(pagination.currentPage);
      } else {
        dispatch(
          addNotification({
            type: "error",
            message: result.message || "Error suspending user",
          })
        );
      }
    } catch (err) {
      console.error(err);
      dispatch(
        addNotification({
          type: "error",
          message: err.message || "Error suspending user",
        })
      );
    }
  };

  // Handle Unsuspend User
  const handleUnsuspendUser = async () => {
    if (!searchedUser) {
      dispatch(
        addNotification({
          type: "warning",
          message: "Please search for a user first",
        })
      );
      return;
    }

    try {
      const result = await userService.unsuspendUser(searchedUser.email);

      if (result.success) {
        dispatch(
          addNotification({
            type: "success",
            message: `User ${searchedUser.email} unsuspended successfully!`,
          })
        );

        // Reset form and switch back to add mode
        switchActionMode("add");

        // Refresh users list
        fetchUsers(pagination.currentPage);
      } else {
        dispatch(
          addNotification({
            type: "error",
            message: result.message || "Error unsuspending user",
          })
        );
      }
    } catch (err) {
      console.error(err);
      dispatch(
        addNotification({
          type: "error",
          message: err.message || "Error unsuspending user",
        })
      );
    }
  };

  // --- API FUNCTION: GET USERS ---
  const fetchUsers = useCallback(async (page = 1) => {
    setLoading(true);
    const pageNum = parseInt(page, 10);
    try {
      const result = await userService.getAllUsers(pageNum, FETCH_LIMIT);

      if (result.success) {
        setUsers(result.data); // Update state with real DB data

        // Update pagination state
        if (result.pagination) {
          setPagination({
            currentPage: parseInt(result.pagination.page, 10),
            totalPages: parseInt(result.pagination.totalPages, 10),
            totalUsers: parseInt(result.pagination.total, 10),
          });
        }
        setError(null);
      } else {
        console.error("Failed to load users:", result.message);
        setError("Failed to load users");
      }
    } catch (err) {
      console.error(err);
      setError(err.message || "Error connecting to server.");
    } finally {
      setLoading(false);
    }
  }, []);

  // --- API FUNCTION: GET ROLES ---
  const fetchRoles = useCallback(async () => {
    try {
      const result = await userService.getRoles();
      if (result.success) {
        const formattedRoles = result.data.map((role) => {
          const rawName = role.name || role;
          const upperName = typeof rawName === 'string' ? rawName.toUpperCase() : ''; 
          // Normalize value using legacy map
          const normalizedValue = LEGACY_ROLE_MAP[upperName] || rawName;
          
          return {
            label: role.name || role,
            value: normalizedValue, 
          };
        });
        setRoles(formattedRoles);
      } else {
        console.error("Failed to load roles:", result.message);
        dispatch(
          addNotification({ type: "error", message: "Failed to load roles" })
        );
      }
    } catch (err) {
      console.error(err);
      dispatch(
        addNotification({ type: "error", message: "Error fetching roles" })
      );
    }
  }, [dispatch]);

  // Load users and roles on component mount
  useEffect(() => {
    fetchUsers(1);
    fetchRoles();
  }, [fetchUsers, fetchRoles]);

  // --- API FUNCTION: ADD USER ---
  const handleAddUser = async () => {
    // Validation
    if (!newUser.full_name || !newUser.email) {
      dispatch(
        addNotification({
          type: "warning",
          message: "Please enter Name and Email",
        })
      );
      return;
    }

    try {
      // ΓÜí OPTIMIZATION: Frontend Check
      const existingUserStr = newUser.email.toLowerCase();
      const localDuplicate = users.find(
        (u) =>
          u.email.toLowerCase() === existingUserStr &&
          (u.roles?.includes(newUser.role) || u.role === newUser.role)
      );

      if (localDuplicate) {
        dispatch(
          addNotification({
            type: "error",
            message: `User already has the role ${newUser.role}`,
          })
        );
        return;
      }

      const result = await userService.createUser(newUser);

      if (result.success) {
        if (result.data.role_already_existed) {
          dispatch(
            addNotification({
              type: "error",
              message: `User already has the role ${newUser.role}`,
            })
          );
        } else {
          dispatch(
            addNotification({
              type: "success",
              message: "User created/updated successfully!",
            })
          );

          // If creating new user, refresh page 1 to show latest
          setNewUser({ full_name: "", role: "Student", email: "" }); // Reset Form
          fetchUsers(1);
        }
      } else {
        dispatch(
          addNotification({
            type: "error",
            message: result.message || "Failed to create user",
          })
        );
      }
    } catch (err) {
      console.error(err);
      dispatch(
        addNotification({
          type: "error",
          message: err.message || "Error connecting to server",
        })
      );
    }
  };

  return (
    <div className="space-y-6 animate-in fade-in duration-500">
      <div className="flex justify-between items-end mb-2">
        <div>
          <h2 className="text-3xl font-bold text-slate-900">User Management</h2>
          <p className="text-slate-500 mt-1">
            Add and manage users for SSO access via Database.
          </p>
        </div>
        {/* Refresh Button */}
        <button
          onClick={() => fetchUsers(pagination.currentPage)}
          className="p-2 text-blue-600 hover:bg-blue-50 rounded-full"
          title="Refresh Data"
        >
          <RefreshCw className={`h-5 w-5 ${loading ? "animate-spin" : ""}`} />
        </button>
      </div>

      {/* --- DYNAMIC USER MANAGEMENT FORM --- */}
      <Card className="mb-6">
        <h3 className="font-bold text-lg text-slate-800 mb-4">
          {actionMode === "add" && "Add New User"}
          {actionMode === "edit" && "Edit User"}
          {actionMode === "suspend" && "Suspend User"}
        </h3>

        {/* ADD USER FORM */}
        {actionMode === "add" && (
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
            <div>
              <label className="block text-xs font-semibold text-slate-500 mb-1">
                Full Name
              </label>
              <input
                type="text"
                className="w-full p-2.5 border border-slate-200 rounded-xl text-sm bg-slate-50 focus:ring-2 focus:ring-blue-500 outline-none transition-shadow"
                placeholder="e.g. John Tan"
                value={newUser.full_name}
                onChange={(e) =>
                  setNewUser({ ...newUser, full_name: e.target.value })
                }
              />
            </div>
            <div>
              <label className="block text-xs font-semibold text-slate-500 mb-1">
                Email (SSO ID)
              </label>
              <input
                type="email"
                className="w-full p-2.5 border border-slate-200 rounded-xl text-sm bg-slate-50 focus:ring-2 focus:ring-blue-500 outline-none transition-shadow"
                placeholder="user@krimson.edu"
                value={newUser.email}
                onChange={(e) =>
                  setNewUser({ ...newUser, email: e.target.value })
                }
              />
            </div>
            <div>
              <label className="block text-xs font-semibold text-slate-500 mb-1">
                Role
              </label>
              <select
                className="w-full p-2.5 border border-slate-200 rounded-xl text-sm bg-slate-50 focus:ring-2 focus:ring-blue-500 outline-none transition-shadow"
                value={newUser.role}
                onChange={(e) =>
                  setNewUser({ ...newUser, role: e.target.value })
                }
              >
                {[
                  { label: "Select Role", value: "" },
                  ...(roles.length > 0
                    ? roles
                    : VALID_ROLES),
                ].map((r) => (
                  <option key={r.value} value={r.value}>
                    {r.label}
                  </option>
                ))}
              </select>
            </div>

            {/* Split Button - Action + Dropdown */}
            <ActionDropdown
              mainLabel="Add User"
              MainIcon={UserPlus}
              onMainAction={handleAddUser}
              actionMode={actionMode}
              onSwitchMode={switchActionMode}
              mainBtnClass="bg-gradient-to-r from-cyan-500 to-blue-500 hover:from-cyan-600 hover:to-blue-600 shadow-blue-500/20"
            />
          </div>
        )}

        {/* EDIT USER FORM */}
        {actionMode === "edit" && (
          <div className="space-y-4">
            {/* Search Section */}
            <UserSearchPanel
              searchEmail={searchEmail}
              setSearchEmail={setSearchEmail}
              handleSearchUser={handleSearchUser}
              searching={searching}
              searchError={searchError}
              theme="blue"
            >
              <ActionDropdown
                mainLabel="Edit"
                MainIcon={Edit3}
                onMainAction={handleEditUser}
                actionMode={actionMode}
                onSwitchMode={switchActionMode}
                disabled={!searchedUser}
                mainBtnClass="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 shadow-blue-500/20"
              />
            </UserSearchPanel>

            {/* User Found - Edit Form */}
            {searchedUser && (
              <div className="space-y-4 p-4 bg-green-50 border border-green-200 rounded-xl animate-in slide-in-from-top-2 duration-200">
                <div className="flex items-center gap-2 text-green-700 font-bold mb-4">
                  <CheckSquare className="h-5 w-5" />
                  User Found!
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-xs font-semibold text-slate-600 mb-1">
                      Full Name
                    </label>
                    <input
                      type="text"
                      className="w-full p-3 border border-slate-300 rounded-xl text-sm bg-white focus:ring-2 focus:ring-blue-500 outline-none transition-shadow"
                      value={editForm.full_name}
                      onChange={(e) =>
                        setEditForm({ ...editForm, full_name: e.target.value })
                      }
                    />
                  </div>

                  <div>
                    <label className="block text-xs font-semibold text-slate-600 mb-1">
                      Email
                    </label>
                    <input
                      type="email"
                      className="w-full p-3 border border-slate-300 rounded-xl text-sm bg-slate-100 focus:ring-2 focus:ring-blue-500 outline-none transition-shadow"
                      value={editForm.email}
                      disabled
                      title="Email cannot be changed"
                    />
                  </div>

                  <RoleMultiSelector
                    selectedRoles={editForm.roles}
                    onChange={(newRoles) =>
                      setEditForm({ ...editForm, roles: newRoles })
                    }
                    roles={
                      roles.length > 0
                        ? roles.map((r) => ({ ...r, value: r.value }))
                        : undefined
                    }
                  />
                </div>

                {/* Update Button */}
                <button
                  onClick={handleEditUser}
                  className="w-full p-3 bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-xl font-bold text-sm hover:from-blue-700 hover:to-indigo-700 transition-all shadow-lg flex items-center justify-center gap-2"
                >
                  <CheckSquare className="h-4 w-4" />
                  Update User
                </button>
              </div>
            )}
          </div>
        )}

        {/* SUSPEND USER FORM */}
        {actionMode === "suspend" && (
          <div className="space-y-4">
            {/* Search Section */}
            <UserSearchPanel
              searchEmail={searchEmail}
              setSearchEmail={setSearchEmail}
              handleSearchUser={handleSearchUser}
              searching={searching}
              searchError={searchError}
              theme="red"
              placeholder="user@example.com or user_id"
            >
                 <ActionDropdown
                    mainLabel="Suspend Mode"
                    MainIcon={UserX}
                    onMainAction={() => {}} // No top-level action
                    actionMode={actionMode}
                    onSwitchMode={switchActionMode}
                    disabled={true} 
                    mainBtnClass="bg-gradient-to-r from-red-500 to-orange-500 opacity-80 cursor-default"
                    toggleBtnClass="bg-gradient-to-r from-red-500 to-orange-500 hover:from-red-600 hover:to-orange-600"
                 />
            </UserSearchPanel>

            {/* User Found - Show Details */}
            {searchedUser && (
              <div className="space-y-4 p-6 bg-gradient-to-br from-slate-50 to-slate-100 border-2 border-slate-200 rounded-2xl animate-in slide-in-from-top-2 duration-200">
                {/* Header */}
                <div className="flex items-center justify-between pb-4 border-b border-slate-300">
                  <div className="flex items-center gap-3">
                    <div className="w-14 h-14 rounded-full bg-gradient-to-br from-red-600 to-orange-600 flex items-center justify-center text-white font-bold text-xl shadow-lg">
                      {searchedUser.full_name?.charAt(0).toUpperCase() || "U"}
                    </div>
                    <div>
                      <h3 className="text-lg font-bold text-slate-800">
                        {searchedUser.full_name || "N/A"}
                      </h3>
                      <p className="text-sm text-slate-600">
                        {searchedUser.email}
                      </p>
                    </div>
                  </div>
                  <div
                    className={`px-4 py-2 rounded-lg font-bold text-sm ${
                      searchedUser.status === "suspended"
                        ? "bg-red-100 text-red-700 border-2 border-red-300"
                        : searchedUser.status === "active"
                        ? "bg-green-100 text-green-700 border-2 border-green-300"
                        : "bg-gray-100 text-gray-700 border-2 border-gray-300"
                    }`}
                  >
                    {searchedUser.status?.toUpperCase() || "UNKNOWN"}
                  </div>
                </div>

                {/* Details Grid */}
                <div className="grid grid-cols-2 gap-4">
                  <div className="bg-white p-4 rounded-xl border border-slate-200">
                    <p className="text-xs font-semibold text-slate-500 mb-1">
                      User ID
                    </p>
                    <p className="text-sm font-mono text-slate-800 truncate">
                      {searchedUser.user_id || "N/A"}
                    </p>
                  </div>
                  <div className="bg-white p-4 rounded-xl border border-slate-200">
                    <p className="text-xs font-semibold text-slate-500 mb-1">
                      Active Role
                    </p>
                    <p className="text-sm font-bold text-slate-800">
                      {searchedUser.active_role || searchedUser.role || "N/A"}
                    </p>
                  </div>
                  {searchedUser.status === "suspended" &&
                    searchedUser.updated_at && (
                      <div className="col-span-2 bg-red-50 p-4 rounded-xl border-2 border-red-200">
                        <p className="text-xs font-semibold text-red-600 mb-1">
                          ΓÜá∩╕Å Suspended On
                        </p>
                        <p className="text-sm font-bold text-red-800">
                          {new Date(searchedUser.updated_at).toLocaleString(
                            "en-US",
                            {
                              dateStyle: "medium",
                              timeStyle: "short",
                            }
                          )}
                        </p>
                      </div>
                    )}
                </div>

                {/* Action Button */}
                {searchedUser.status === "suspended" ? (
                  <button
                    onClick={handleUnsuspendUser}
                    className="w-full p-4 bg-gradient-to-r from-green-600 to-emerald-600 text-white rounded-xl font-bold text-sm hover:from-green-700 hover:to-emerald-700 transition-all shadow-lg flex items-center justify-center gap-2"
                  >
                    <CheckSquare className="h-5 w-5" />
                    Unsuspend User
                  </button>
                ) : (
                  <button
                    onClick={handleSuspendUser}
                    className="w-full p-4 bg-gradient-to-r from-red-600 to-orange-600 text-white rounded-xl font-bold text-sm hover:from-red-700 hover:to-orange-700 transition-all shadow-lg flex items-center justify-center gap-2"
                  >
                    <UserX className="h-5 w-5" />
                    Confirm Suspend User
                  </button>
                )}
              </div>
            )}
          </div>
        )}
      </Card>

      {/* --- USERS TABLE (Dynamic) --- */}
      <UserListTable
        users={users}
        loading={loading}
        error={error}
        localPage={localPage}
        setLocalPage={setLocalPage}
        rowsPerPage={ROWS_PER_PAGE}
      />
    </div>
  );
};

export default UserManagement;

