import React from 'react';
import { SYSTEM_ADMIN_DATA } from '../../../data/systemAdminData';
import { Activity, Server, Cpu, Wifi, AlertTriangle, CheckCircle } from 'lucide-react';

const StatCard = ({ title, value, icon: Icon, color, subtext }) => (
  <div className="bg-white p-6 rounded-3xl shadow-sm border-b-4 relative overflow-hidden group" style={{ borderColor: color }}>
    <div className="flex justify-between items-start relative z-10">
      <div>
        <p className="text-slate-400 text-xs font-bold uppercase tracking-wider">{title}</p>
        <h3 className="text-3xl font-extrabold text-slate-800 mt-2">{value}</h3>
        <p className="text-xs text-slate-400 mt-2">{subtext}</p>
      </div>
      <div className="p-3 rounded-2xl bg-slate-50 text-slate-500 group-hover:bg-slate-100 transition-colors">
        <Icon size={24} />
      </div>
    </div>
  </div>
);

const SystemHealthMonitoring = () => {
  const { health } = SYSTEM_ADMIN_DATA;

  return (
    <div className="space-y-8">
      {/* 1. Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <StatCard title="System Uptime" value={health.uptime} icon={Server} color="#10b981" subtext="Last 30 days" />
        <StatCard title="CPU Usage" value={`${health.cpuUsage}%`} icon={Cpu} color="#3b82f6" subtext="4 Cores Active" />
        <StatCard title="Memory Usage" value={`${health.memoryUsage}%`} icon={Activity} color="#f59e0b" subtext="16GB / 24GB Used" />
        <StatCard title="Active Sessions" value={health.activeSessions} icon={Wifi} color="#8b5cf6" subtext="Concurrent Users" />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        
        {/* 2. Real-time Alerts */}
        <div className="lg:col-span-2 bg-white rounded-3xl p-8 shadow-sm">
          <h3 className="font-bold text-slate-800 mb-6 flex items-center gap-2">
            <AlertTriangle className="text-red-500" /> System Alerts & Notifications
          </h3>
          <div className="space-y-4">
             {health.alerts.map((alert) => (
               <div key={alert.id} className={`p-4 rounded-xl border flex items-center justify-between ${alert.type === 'error' ? 'bg-red-50 border-red-100' : 'bg-orange-50 border-orange-100'}`}>
                 <div className="flex items-center gap-3">
                   <span className={`w-2 h-2 rounded-full ${alert.type === 'error' ? 'bg-red-500' : 'bg-orange-500'}`}></span>
                   <div>
                     <p className={`text-sm font-bold ${alert.type === 'error' ? 'text-red-700' : 'text-orange-700'}`}>{alert.msg}</p>
                     <p className="text-xs text-slate-400">{alert.time}</p>
                   </div>
                 </div>
                 <button className="text-xs font-bold px-3 py-1 bg-white rounded-lg shadow-sm text-slate-500 hover:text-slate-800">Resolve</button>
               </div>
             ))}
             {health.alerts.length === 0 && (
               <div className="p-8 text-center text-slate-400">
                 <CheckCircle className="mx-auto mb-2 text-green-400" size={32} />
                 <p>All systems operational. No active alerts.</p>
               </div>
             )}
          </div>
        </div>

        {/* 3. API Response Heatmap Mock */}
        <div className="bg-white rounded-3xl p-8 shadow-sm">
          <h3 className="font-bold text-slate-800 mb-2">API Latency</h3>
          <p className="text-xs text-slate-400 mb-6">Average Response Time: <span className="font-bold text-slate-800">{health.apiResponseTime}ms</span></p>
          
          <div className="grid grid-cols-5 gap-2">
            {[...Array(25)].map((_, i) => {
               // Mock heatmap colors based on random latency
               const latency = 50 + ((i * 17) % 200); // Stable mock latency based on index 
               let bgClass = "bg-green-400";
               if (latency > 150) bgClass = "bg-yellow-400";
               if (latency > 220) bgClass = "bg-red-400";
               
               return (
                 <div key={i} className={`h-8 rounded-md ${bgClass} opacity-80 hover:opacity-100 transition-opacity cursor-pointer`} title={`Latency: ${Math.floor(latency)}ms`}></div>
               );
            })}
          </div>
          <div className="flex justify-between mt-4 text-xs text-slate-400">
            <span className="flex items-center gap-1"><div className="w-2 h-2 bg-green-400 rounded-full"></div> &lt;150ms</span>
            <span className="flex items-center gap-1"><div className="w-2 h-2 bg-yellow-400 rounded-full"></div> 150-220ms</span>
            <span className="flex items-center gap-1"><div className="w-2 h-2 bg-red-400 rounded-full"></div> &gt;220ms</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SystemHealthMonitoring;

import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { DateTime } from "luxon";
import {
    LayoutDashboard,
    Bell,
    CheckSquare,
    FileText,
    User,
    LogOut,
    Calendar,
    School
} from "lucide-react";
import { authService } from "../../services/authService";
import RoleSwitcher from "../../components/common/RoleSwitcher";
import { useAppDispatch } from "../../store/hooks";
import { addNotification } from "../../store/slices/uiSlice";
import { getDashboardPath } from "../../utils/roleNavigation";
import { getUserActiveRole, formatRoleForDisplay } from "../../utils/roleUtils";

const WelcomeLanding = () => {
    const navigate = useNavigate();
    const dispatch = useAppDispatch();
    const [user, setUser] = useState(() => authService.getCurrentUser());
    const [time, setTime] = useState(DateTime.now().setZone("Asia/Singapore"));

    useEffect(() => {
        if (!user) {
            navigate("/login");
            return;
        }

        // Clock ticker
        const timer = setInterval(() => {
            setTime(DateTime.now().setZone("Asia/Singapore"));
        }, 1000);

        return () => clearInterval(timer);
    }, [navigate]);

    const handleLogout = () => {
        authService.logout();
        dispatch(addNotification({ type: 'info', message: 'Logged out successfully' }));
        navigate("/login");
    };

    if (!user) return null;

    // Use utility function to safely get user's active role
    const activeRole = getUserActiveRole(user);
    const dashboardPath = getDashboardPath(activeRole);

    // Mock Announcements
    const announcements = [
        { id: 1, text: "Annual Sports Day on 15th Jan", date: "2 days ago" },
        { id: 2, text: "Parent-Teacher meeting scheduled", date: "5 hours ago" },
        { id: 3, text: "New Library books available", date: "Just now" },
    ];

    return (
        <div className="min-h-screen bg-gradient-to-br from-cyan-50 via-blue-50 to-pink-50 font-sans text-slate-800">
            {/* Navbar / Header */}
            <nav className="px-6 py-4 bg-white/80 backdrop-blur border-b border-white/50 shadow-sm flex items-center justify-between sticky top-0 z-50">
                <div className="flex items-center gap-3">
                    <div className="w-10 h-10 bg-gradient-to-br from-cyan-400 via-blue-500 to-pink-500 rounded-xl flex items-center justify-center text-white shadow-lg shadow-blue-600/20">
                        <School className="h-6 w-6" />
                    </div>
                    <span className="font-bold text-xl tracking-tight text-slate-800">
                        Krimson OS
                    </span>
                </div>
                <div className="flex items-center gap-4">
                    <RoleSwitcher currentUser={user} />
                    <div className="h-8 w-[1px] bg-slate-200"></div>
                    <button
                        onClick={handleLogout}
                        className="text-slate-500 hover:text-rose-600 transition-colors flex items-center gap-2 text-sm font-medium"
                    >
                        <LogOut className="h-4 w-4" />
                        Logout
                    </button>
                </div>
            </nav>

            {/* Main Content */}
            <main className="max-w-7xl mx-auto p-6 sm:p-10">

                {/* Welcome Section */}
                <div className="relative overflow-hidden rounded-[2.5rem] bg-gradient-to-br from-cyan-400 via-blue-400 to-pink-400 text-white shadow-2xl shadow-blue-900/20 p-8 sm:p-12 mb-10">
                    {/* Background Decorations */}
                    <div className="absolute top-0 right-0 w-64 h-64 bg-white/10 rounded-full blur-3xl -mr-16 -mt-16 pointer-events-none"></div>
                    <div className="absolute bottom-0 left-0 w-48 h-48 bg-pink-500/20 rounded-full blur-2xl -ml-10 -mb-10 pointer-events-none"></div>

                    <div className="relative z-10 flex flex-col md:flex-row justify-between items-start md:items-end gap-8">
                        <div>
                            <div className="flex items-center gap-3 mb-4">
                                <div className="w-16 h-16 rounded-full bg-white/20 backdrop-blur border border-white/30 flex items-center justify-center text-xl font-bold">
                                    {user.name.charAt(0)}
                                </div>
                                <div>
                                    <p className="text-white/80 font-medium text-lg">Welcome back,</p>
                                    <h1 className="text-3xl sm:text-4xl font-bold">{user.name}</h1>
                                </div>
                            </div>
                            <div className="inline-flex items-center gap-2 px-4 py-1.5 rounded-full bg-white/20 backdrop-blur border border-white/10 text-sm font-medium">
